coachView.loadJsFunction:
 var cv = this;

 var cvBinding = cv.context.binding.get("value");
 
 require(["jquery"], function($){
 	require(["bootstrap"], function(){
 		$("#"+cv.context.viewid + "_doSomething").on("click", function() {
 			cv.context.trigger();
 		});
 		
 		$("#"+cv.context.viewid + "_userName").on("change", function(evt) {
 			var local = cv.context.binding.get("value");
 			console.log("Username Changed", local, evt, this);
 			cvBinding.name = $(this).val();
 		});
 	});
 });

