var cv = this;
console.log("CV1", cv);

cv.updateProgressBars = function($scope) {
	 $scope.progressBars = [];
		    for(var i=0; i<$scope.bindingValue; i++) {
			var bar = {
				value: Math.ceil(Math.random()*100)
			}
			$scope.progressBars.push(bar);
		    }
}

angular
	.module("responsive.coaches")
	.controller("progressBar", ["$scope", "$element", "$timeout","Coach", function 
		($scope, $element, $timeout, Coach)
	{
		"use strict";
		//console.log("Scope", $scope, $scope.bindingValue);
		angular.extend($scope, new Coach($scope, $element, $timeout));
		$scope.progressBars = [];
		
		$scope.watchOption("maxValue", 100);
		$scope.watchOption("minValue", 0);
		cv.$scope = $scope;
		
		$scope.$watch($scope.bindingValue, function(){
			cv.updateProgressBars($scope);
		});
		
		console.log("CV2", cv);
}]);
